CREATE FUNCTION _st_tpi4ma(value double precision[], pos integer[], VARIADIC userargs text[] DEFAULT NULL::text[]) RETURNS double precision
    LANGUAGE plpgsql IMMUTABLE
    AS $$
	DECLARE
		x integer;
