st_x(g_dbstorm.the_geom) + (sin((g_dbstorm.dd) * pi()/180.) * ftime * 1000. * g_dbstorm.vv) || ' ' || st_y(g_dbstorm.the_geom) + (cos((g_dbstorm.dd) * pi()/180.) * ftime  * 1000. * g_dbstorm.vv) || ' , ' ||
