		WHEN (NEW.valore > dmv_deroga) OR (NEW.valore < dmv_base) THEN 1
		WHEN NEW.valore < dmv_deroga THEN 2
		ELSE 3
	END
	WHERE NEW.codice=codice;
END IF;
END IF;

